import React, { useEffect, useState } from "react";
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  Button,
  Dimensions,
} from "react-native";
import ModalBox from "react-native-modalbox";
import { AntDesign } from "@expo/vector-icons";
import { LineChart } from "react-native-chart-kit";
import DateTimePicker from "@react-native-community/datetimepicker";
import { GetAllMeasurementDateByPlantID } from "../api/Requests";

const FontSize = require("../assets/styles/fonts/FontSize");
const RADIUS = 10;

const MyLineChart = ({ values }) => {
  return (
    <LineChart
      data={{
        datasets: [
          {
            data: values,
            strokeWidth: 2,
          },
        ],
      }}
      width={Dimensions.get("window").width - 30}
      height={Dimensions.get("window").height - 250}
      chartConfig={{
        backgroundColor: "#1cc910",
        backgroundGradientFrom: "#eff3ff",
        backgroundGradientTo: "#efefef",
        decimalPlaces: 2,
        color: (opacity = 1) => `rgba(0, 0, 0, ${opacity})`,
        style: {
          borderRadius: 16,
        },
      }}
      style={{
        marginVertical: 8,
        borderRadius: 16,
      }}
    />
  );
};

export default function DetailsModal({ open, closeModal, plantID }) {
  const [date, setDate] = useState(new Date());
  const [show, setShow] = useState(false);
  let datas = [0]
  console.log(plantID);

  const onChange = (event, selectedDate) => {
    const currentDate = selectedDate || date;
    setShow(Platform.OS === "ios");
    setDate(currentDate);
    GetAllMeasurementDateByPlantID(plantID, currentDate).then((result) => {
      result.map(m => {
        datas.push(m.airQuality)
      })
    });
  };

  const showDatepicker = () => {
    setShow(true);
  };

  return (
    <ModalBox
      isOpen={open}
      onClosed={closeModal}
      position={"bottom"}
      entry={"bottom"}
      style={styles.modal}
      backButtonClose={true}
    >
      <View style={styles.closeButtonView}>
        <TouchableOpacity style={styles.modalButton} onPress={closeModal}>
          <AntDesign name="close" size={24} color="black" />
        </TouchableOpacity>
      </View>
      <View style={styles.modalBodyView}>
        <MyLineChart values={datas} />
        <Text>Se√ßilen tarihteki 24 saatlik veri</Text>
        <Button onPress={showDatepicker} title="Show date picker!" />
        {show && (
          <DateTimePicker
            testID="dateTimePicker"
            value={date}
            mode={"date"}
            is24Hour={true}
            display="default"
            onChange={onChange}
          />
        )}
      </View>
    </ModalBox>
  );
}

const styles = StyleSheet.create({
  modal: {
    flex: 1,
    backgroundColor: "#FFF",
  },
  closeButtonView: {
    flex: 0.5,
  },
  modalBodyView: {
    flex: 9,
    alignItems: "center",
    justifyContent: "center",
  },
  modalButton: {
    alignSelf: "flex-end",
    margin: "5%",
  },
});
